def expand(val, nums, x, y, z):
    dirs = {z}
    for num in nums.copy():
        if abs(val - num) not in (1, 5) or {val % 5, num % 5} == {0, 1}:
            continue
        nums.discard(num)
        diff = val - num
        sign = diff // abs(diff)
        nx, ny, nz = (x, z * sign, -y * sign) if abs(diff) == 1 else (-z * sign, y, x * sign)
