def factorize_add(num):
    if num < 4:
        return num
    d = 2
    p = 0
    while d < num ** 0.5 + 1:
        while not num % d:
            p += d
            num /= d
        d += 1 if d == 2 else 2
    return p if num == 1 else p + num

