from collections import deque
import re
TOKENIZER = re.compile('(R+|F+|L+|\\)|\\()(\\d*)')

def parseCode(code):
    cmds = [[]]
    for cmd, n in TOKENIZER.findall(code):
        s, r = (cmd[0], int(n or '1') + len(cmd) - 1)
        if cmd == '(':
            cmds.append([])
        elif cmd == ')':
            lst = cmds.pop()
            cmds[-1].extend(lst * r)
        else:
            cmds[-1] += [(s, r)]
    return cmds[0]

def execute(code):
    pos, dirs = ((0, 0), deque([(0, 1), (1, 0), (0, -1), (-1, 0)]))
    seens = {pos}
    for s, r in parseCode(code):
        if s == 'F':
            for _ in range(r):
                pos = tuple((z + dz for z, dz in zip(pos, dirs[0])))
                seens.add(pos)
        else:
            dirs.rotate(r % 4 * (-1) ** (s == 'R'))
    miX, maX = (min((x for x, y in seens)), max((x for x, y in seens)))
    miY, maY = (min((y for x, y in seens)), max((y for x, y in seens)))
    return '\r\n'.join((''.join(('*' if (x, y) in seens else ' ' for y in range(miY, maY + 1))) for x in range(miX, maX + 1)))

