import random

def even_odd(n):
    s, d = (0, n)
    while d % 2 == 0:
        s += 1
        d >>= 1
    return (s, d)

def Miller_Rabin(a, p):
    s, d = even_odd(p - 1)
    a = pow(a, d, p)
    if a == 1:
        return True
    for i in range(s):
        if a == p - 1:
            return True
        a = pow(a, 2, p)
    return False

def is_prime(p):
    if p == 2:
        return True
    if p <= 1 or p % 2 == 0:
        return False
    return all((Miller_Rabin(random.randint(2, p - 1), p) for _ in range(40)))

def complete_binary_tree(a):

    def in_order(n=0):
        if n < len(a):
            yield from in_order(2 * n + 1)
            yield n
            yield from in_order(2 * n + 2)
    result = [None] * len(a)
    for i, x in zip(in_order(), a):
        result[i] = x
    return result

