def amazon_check_mate(*args):

    def posInBoard(x, y):
        return 0 <= x < 8 and 0 <= y < 8

    def getCoveredPos(start, king=None):
        covered = {start}
        for m in AMA_MOVES if king else ALL_MOVES:
            pos = tuple((z + dz for z, dz in zip(start, m)))
            if posInBoard(*pos):
