    perms = [float(''.join(e)) for e in permutations(str(n))]
    return int(round(sum(perms) / len(perms)))

def isPrime(n):
    return n == 2 or (n > 2 and n & 1 and all((n % p for p in range(3, int(n ** 0.5 + 1), 2))))

def prime_product(n):
    return next((x * (n - x) for x in range(n >> 1, 1, -1) if isPrime(x) and isPrime(n - x)), 0)

def pass_the_bill(total, conservative, reformist):
    ind = total - conservative - reformist
    majority = total // 2 + 1
    if conservative > majority:
        return 0
    elif conservative + ind < majority:
        return -1
    else:
        return majority - conservative

def count_correct_characters(c, g):
    if len(c) != len(g):
        raise Exception('Error')
    return sum((1 for i, j in zip(c, g) if i == j))

def riders(stations):
    riders, travelled = (1, 0)
    for dist in stations:
        if travelled + dist > 100:
            riders += 1
            travelled = dist
        else:
            travelled += dist
    return riders

from fractions import Fraction

def sum_fracts(lst):
    if lst:
        ret = sum((Fraction(a, b) for a, b in lst))
        return ret.numerator if ret.denominator == 1 else [ret.numerator, ret.denominator]

def remainder(dividend, divisor):
    while divisor <= dividend:
        dividend = dividend - divisor
    return dividend

def palindrome(num):
    if type(num) != int or num < 0:
        return 'Not valid'
    s = str(num)
    for i in range(len(s) - 2):
        if s[i] == s[i + 1] or s[i] == s[i + 2]:
            return True
    return len(s) != 1 and s[-1] == s[-2]

def sort_time(arr):
    arr, s = (sorted(arr, key=lambda t: t[0]), [])
    while arr:
        nextTP = next((i for i, t in enumerate(arr) if not s or t[0] >= s[-1][1]), 0)
        s.append(arr.pop(nextTP))
    return s

def boxes_packing(l, w, h):
    boxes = sorted((sorted(t) for t in zip(l, w, h)))
    return all((s < l for b1, b2 in zip(boxes[:-1], boxes[1:]) for s, l in zip(b1, b2)))

