TO_KELVIN = {'C': (1, 273.15), 'F': (5.0 / 9, 459.67 * 5.0 / 9), 'R': (5.0 / 9, 0), 'De': (-2.0 / 3, 373.15), 'N': (100.0 / 33, 273.15), 'Re': (5.0 / 4, 273.15), 'Ro': (40.0 / 21, -7.5 * 40 / 21 + 273.15)}

def convert_temp(temp, from_scale, to_scale):
    if from_scale == to_scale:
        return temp
    if from_scale != 'K':
        a, b = TO_KELVIN[from_scale]
        temp = a * temp + b
        if to_scale == 'K':
            return int(round(temp))
    a, b = TO_KELVIN[to_scale]
    return int(round((temp - b) / a))

def reflections(n, m):
    x = y = 0
    dx = dy = 1
    while 1:
        x += dx
        y += dy
        if x == y == 0 or (x == n and y == m):
            return 1
        if 0 in (x, y) and (x == n or y == m):
            return 0
        if x in (0, n):
            dx *= -1
        if y in (0, m):
            dy *= -1

