def get_electron_configuration(element):
    elt, repl = EXCEPTIONS.get(element, (element, []))
    z, nl, config = (ELT_TO_Z[elt], 0, {})
    while z:
        nl += 1
        for l in range(nl - 1 >> 1, -1, -1):
            nE = min(z, 2 + l * 4)
            config[nl - l, l] = nE
            z -= nE
            if not z:
                break
    for a, b, n in repl:
        config[a, b] = n
    s = ' '.join((f'{k[0]}{ORBITALS[k[1]]}{n}' for k, n in sorted(config.items())))
    return f'{element} -> {s}'

