import math

def checksquares(N):
    flag = 0
    for a in range(int(math.sqrt(N)) + 1):
        for b in range(int(math.sqrt(N)) + 1):
            if a * a + b * b == N:
                flag = 1
                break
        if flag == 1:
            break
    return flag

def dig_pow(n, p):
    s = 0
    for i, c in enumerate(str(n)):
        s += pow(int(c), p + i)
    return s / n if s % n == 0 else -1

def deep_count(a):
    result = 0
    for i in range(len(a)):
        if type(a[i]) is list:
            result += deep_count(a[i])
        result += 1
    return result

def array_conversion(arr):
    sign = 0
    while len(arr) > 1:
        sign = 1 ^ sign
        arr = list(map(lambda x, y: x + y, arr[0::2], arr[1::2]) if sign else map(lambda x, y: x * y, arr[0::2], arr[1::2]))
    return arr[0]

import re

def scramble_words(words):

    def sort_letters(match):
        s = match.group()
        letters = iter(sorted(filter(str.isalpha, s[1:-1])))
        return s[0] + ''.join((next(letters) if c.isalpha() else c for c in s[1:-1])) + s[-1]
    return re.sub('[a-z][^\\s]*[a-z]', sort_letters, words)

