def solomons_quest(arr):
    pos, lvl = ([0, 0], 0)
    for dilat, dir, dist in arr:
        lvl += dilat
        pos[dir in [0, 2]] += dist * 2 ** lvl * (-1) ** (dir in [2, 3])
    return pos

def expanded_form(num):
    integer_part, fractional_part = str(num).split('.')
    result = [str(int(num) * 10 ** i) for i, num in enumerate(integer_part[::-1]) if num != '0'][::-1]
    result += [str(num) + '/' + str(10 ** (i + 1)) for i, num in enumerate(fractional_part) if num != '0']
    return ' + '.join(result)

def men_from_boys(arr):
    men = []
    boys = []
    for i in sorted(set(arr)):
        if i % 2 == 0:
            men.append(i)
        else:
            boys.append(i)
    return men + boys[::-1]

def getDivs(n):
    return {1} | {y for x in range(2, int(n ** 0.5) + 1) for y in [n // x, x] if not n % x}

def amicable_numbers(n1, n2):
    return sum(getDivs(n1)) == n2 and sum(getDivs(n2)) == n1

from collections import Counter

def string_letter_count(s):
    cnt = Counter((c for c in s.lower() if c.isalpha()))
    return ''.join((str(n) + c for c, n in sorted(cnt.items())))

def compare(s1, s2):
    v1, v2 = (version(s1), version(s2))
    return -1 if v1 < v2 else 1 if v1 > v2 else 0

def version(s):
    v = [int(n) for n in s.split('.')]
    while v[-1] == 0:
        v = v[0:-1]
    return v

import math

def is_pronic(n):
    return n >= 0 and math.sqrt(1 + 4 * n) % 1 == 0

def palindrome(num, s):
    if not type(num) == type(s) == int or num < 0 or s < 0:
        return 'Not valid'
    ans, num = ([], max(num, 11))
    while len(ans) != s:
        if num == int(str(num)[::-1]):
            ans.append(num)
        num += 1
    return ans

def pattern(n):
    top = [(str(i % 10) * n).center(n * 3 - 2) for i in range(1, n)]
    left = ''.join((str(i % 10) for i in range(1, n)))
    middle = left + str(n % 10) * n + left[::-1]
    return '\n'.join(top + [middle] * n + top[::-1])

def simplify(n):
    for d in range(int(n ** 0.5), 0, -1):
        if not n % d ** 2:
            break
    if d * d == n:
        return '%d' % d
    elif d == 1:
        return 'sqrt %d' % n
    else:
        return '%d sqrt %d' % (d, n // d ** 2)

def desimplify(s):
    x, _, y = s.partition('sqrt')
    return int(x or '1') ** 2 * int(y or '1')

