def productsum(kmax):

    def prodsum2(p, s, c, start):
        k = p - s + c
        if k < kmax:
            if p < n[k]:
                n[k] = p
            for i in range(start, kmax // p * 2 + 1):
                prodsum2(p * i, s + i, c + 1, i)
    kmax += 1
    n = [2 * kmax] * kmax
    prodsum2(1, 1, 1, 2)
    return sum(set(n[2:]))

from math import gcd
from functools import reduce

def dpc_sequence(s):

    def lcm(a, b):
        return a * b // gcd(a, b)
    d, p, c = ([], [], [])
    for i in range(len(s)):
        if s[i] == 'D':
            d.append(i + 1)
        elif s[i] == 'P':
            p.append(i + 1)
        else:
            c.append(i + 1)
    if not d:
        return -1
    lcm_d = reduce(lambda i, j: lcm(i, j), d)
    for i in p:
        if gcd(lcm_d, i) != 1:
            return -1
    for i in c:
        if lcm_d % i == 0 or gcd(lcm_d, i) == 1:
            return -1
    return lcm_d

def find_f1_eq_f2(n, k):
    s = set(range(k))
    while True:
        n += 1
        testn = n
        while True:
            f = set(map(int, str(testn)))
            if f <= s:
                if f == s:
                    return n
                break
            testn += n

def solve(s):
    vowels = sorted((c for c in s if c in 'aeiou'))
    consonants = sorted((c for c in s if c not in 'aeiou'))
    part1, part2 = sorted((vowels, consonants), key=len, reverse=True)
    part2.append('')
    if len(part1) > len(part2):
        return 'failed'
    return ''.join((a + b for a, b in zip(part1, part2)))

def find_even_index(arr):
    for i in range(len(arr)):
        if sum(arr[:i]) == sum(arr[i + 1:]):
            return i
    return -1

def six_column_encryption(msg):
    msg = msg.replace(' ', '.') + '.' * ((6 - len(msg) % 6) % 6)
    return ' '.join((msg[n::6] for n in range(6)))

def two_count(n):
    res = 0
    while not n & 1:
        res += 1
        n >>= 1
    return res

