def kooka_counter(laughing: str) -> int:
    if not laughing:
        return 0
    count = 1
    previous_laugh = laughing[:2]
    for i in range(2, len(laughing), 2):
        current_laugh = laughing[i:i + 2]
        if current_laugh != previous_laugh:
            count += 1
            previous_laugh = current_laugh
    return count

