def valid_mersenne(n: int) -> bool:

    def is_prime(num):
        if num <= 1:
            return False
        if num <= 3:
            return True
        if num % 2 == 0 or num % 3 == 0:
            return False
        i = 5
        while i * i <= num:
            if num % i == 0 or num % (i + 2) == 0:
                return False
            i += 6
        return True
    mersenne_number = (1 << n) - 1
    return is_prime(mersenne_number)

