from typing import List

def mean_vs_median(numbers: List[float]) -> str:
    numbers.sort()
    n = len(numbers)
    median = numbers[n // 2]
    mean = sum(numbers) / n
    if mean > median:
        return 'mean'
    elif median > mean:
        return 'median'
    else:
        return 'same'

