def tv_remote(words: str) -> int:
    keyboard = ['abcde123', 'fghij456', 'klmno789', 'pqrst.@0', 'uvwxyz_/', 'aASP']

    def find_position(char):
        for i, row in enumerate(keyboard):
            if char in row:
                return (i, row.index(char))
        return None
    current_pos = (0, 0)
    total_presses = 0
    shift_pressed = False
    for char in words:
        target_char = char
        if char.isupper() and (not shift_pressed):
            shift_pos = find_position('aA')
            total_presses += abs(shift_pos[0] - current_pos[0]) + abs(shift_pos[1] - current_pos[1]) + 1
            current_pos = shift_pos
            shift_pressed = True
        if char.islower() and shift_pressed:
            shift_pos = find_position('aA')
            total_presses += abs(shift_pos[0] - current_pos[0]) + abs(shift_pos[1] - current_pos[1]) + 1
            current_pos = shift_pos
            shift_pressed = False
        if char == ' ':
            target_char = 'SP'
        target_pos = find_position(target_char)
        total_presses += abs(target_pos[0] - current_pos[0]) + abs(target_pos[1] - current_pos[1]) + 1
        current_pos = target_pos
    return total_presses

def tv_remote(words: str) -> int:
    keyboard = ['abcde123', 'fghij456', 'klmno789', 'pqrst.@0', 'uvwxyz_/']
    position = (0, 0)
    total_presses = 0
    shift_on = False

    def get_position(char):
        for i, row in enumerate(keyboard):
            if char in row:
                return (i, row.index(char))
        return None

    def calculate_distance(pos1, pos2):
        return abs(pos1[0] - pos2[0]) + abs(pos1[1] - pos2[1])
    for char in words:
        if char == ' ':
            target_pos = get_position('SP')
