from typing import List

def istree(matrix: List[List[int]]) -> bool:

    def dfs(node, visited, parent):
        visited[node] = True
        for neighbor in matrix[node]:
            if not visited[neighbor]:
                if not dfs(neighbor, visited, node):
                    return False
            elif neighbor != parent:
                return False
        return True
    n = len(matrix)
    visited = [False] * n
    if not dfs(0, visited, -1):
        return False
    return all(visited)

