from typing import List

def strange_coach(players: List[str]) -> str:
    from collections import defaultdict
    surname_initials = defaultdict(int)
    for player in players:
        initial = player[0]
        surname_initials[initial] += 1
    possible_initials = [initial for initial, count in surname_initials.items() if count >= 5]
    if not possible_initials:
        return 'forfeit'
    return ''.join(sorted(possible_initials))

